		return {'autoRequestMore':true, 'success':true, 'more':true, 'url':tokenUrl, 'httpHeaders':httpHeaders, 'httpParams':httpParams, 'POST':true};
	}

	if(data && data.length>0)
	{
		let urlList = [];
		let len = data.length;
		for(i=0; i<len; i++) {
			let info = data[i];
			if(info.url && info.token){
				let url = info.url + '?token=' + info.token;
